create table submission_history ( submission_history_pk_id bigint(20) not null auto_increment, patient_id varchar(64), study_instance_uid varchar(500), series_instance_uid varchar(64), sop_instance_uid varchar(64), submission_date date NOT NULL, project varchar(200), site varchar(40), operation_type smallint NOT NULL, unique(submission_history_pk_id));
insert into submission_history (patient_id, study_instance_uid, series_instance_uid, submission_date, sop_instance_uid, project, site, operation_type) select g.patient_id, g.study_instance_uid, g.series_instance_uid, g.submission_date, g.sop_instance_uid, g.project, tdp.dp_site_name, 0 from general_image g, trial_data_provenance tdp where g.trial_dp_pk_id = tdp.trial_dp_pk_id;
insert into submission_history (patient_id, study_instance_uid, series_instance_uid, submission_date, sop_instance_uid, project, site, operation_type) select gs.patient_id, s.study_instance_uid, gs.series_instance_uid, a.submission_date, '', tdp.project, tdp.dp_site_name, 2 from annotation a, general_series gs, study s, patient p, trial_data_provenance tdp where a.general_series_pk_id = gs.general_series_pk_id and gs.study_pk_id = s.study_pk_id and s.patient_pk_id = p.patient_pk_id and p.trial_dp_pk_id = tdp.trial_dp_pk_id;
create index project_side_idx on submission_history (project, site);

